// Analyze prompt context and prepare for crew activation
const prompt = $input.first().json.prompt;
const timestamp = new Date().toISOString();

// Extract prompt metadata
const promptContext = {
  text: prompt,
  length: prompt.length,
  timestamp: timestamp,
  domain: detectDomain(prompt),
  complexity: detectComplexity(prompt),
  type: detectPromptType(prompt)
};

// Detect domain from prompt
function detectDomain(prompt) {
  const domains = {
    'machine-learning': ['ml', 'ai', 'model', 'training', 'neural'],
    'security': ['security', 'vulnerability', 'threat', 'attack'],
    'psychology': ['emotion', 'behavior', 'psychology', 'mental'],
    'engineering': ['code', 'architecture', 'system', 'design'],
    'strategy': ['strategy', 'planning', 'leadership', 'management']
  };

  const lowerPrompt = prompt.toLowerCase();
  for (const [domain, keywords] of Object.entries(domains)) {
    if (keywords.some(keyword => lowerPrompt.includes(keyword))) {
      return domain;
    }
  }
  return 'general';
}

// Detect complexity level
function detectComplexity(prompt) {
  const highComplexityKeywords = ['complex', 'advanced', 'sophisticated', 'comprehensive'];
  const mediumComplexityKeywords = ['explain', 'describe', 'analyze', 'compare'];

  const lowerPrompt = prompt.toLowerCase();
  if (highComplexityKeywords.some(keyword => lowerPrompt.includes(keyword))) {
    return 'high';
  } else if (mediumComplexityKeywords.some(keyword => lowerPrompt.includes(keyword))) {
    return 'medium';
  }
  return 'low';
}

// Detect prompt type
function detectPromptType(prompt) {
  const technicalKeywords = ['code', 'algorithm', 'technical', 'system'];
  const creativeKeywords = ['creative', 'design', 'artistic', 'innovative'];
  const analyticalKeywords = ['analyze', 'evaluate', 'compare', 'assess'];
  const empathicKeywords = ['feel', 'emotion', 'empathy', 'understand'];
  const strategicKeywords = ['strategy', 'plan', 'leadership', 'vision'];

  const lowerPrompt = prompt.toLowerCase();
  if (technicalKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'technical';
  if (creativeKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'creative';
  if (analyticalKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'analytical';
  if (empathicKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'empathic';
  if (strategicKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'strategic';
  return 'analytical';
}

return {
  promptContext,
  originalPrompt: prompt,
  processingId: `ah_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// Split prompt context for crew member processing
const promptContext = $input.first().json;
const crewMembers = [
  'Captain Picard',
  'Commander Data', 
  'Counselor Troi',
  'Lieutenant Worf',
  'Commander Riker',
  'Lieutenant Commander La Forge',
  'Doctor Crusher',
  'Lieutenant Commander Tasha Yar',
  'Lieutenant Commander Spock'
];

// Create individual crew member tasks
const crewTasks = crewMembers.map(crewMember => ({
  crewMember,
  promptContext: promptContext.promptContext,
  originalPrompt: promptContext.originalPrompt,
  processingId: promptContext.processingId,
  timestamp: new Date().toISOString()
}));

return crewTasks;

// Optimize LLM selection for crew member
const crewTask = $input.first().json;
const { crewMember, promptContext } = crewTask;

// Crew member expertise mapping
const crewExpertise = {
  'Captain Picard': ['leadership', 'strategy', 'diplomacy', 'ethics'],
  'Commander Data': ['technical-analysis', 'logic', 'computation', 'science'],
  'Counselor Troi': ['empathy', 'psychology', 'counseling', 'emotions'],
  'Lieutenant Worf': ['security', 'tactics', 'combat', 'honor'],
  'Commander Riker': ['leadership', 'tactics', 'diplomacy', 'command'],
  'Lieutenant Commander La Forge': ['engineering', 'technology', 'innovation'],
  'Doctor Crusher': ['medical', 'healing', 'science', 'research'],
  'Lieutenant Commander Tasha Yar': ['security', 'tactics', 'survival'],
  'Lieutenant Commander Spock': ['logic', 'science', 'analysis']
};

// LLM optimization based on context and expertise
function selectOptimalLLM(crewMember, context) {
  const expertise = crewExpertise[crewMember] || ['general'];

  // Context-based LLM selection
  if (context.type === 'technical' && expertise.includes('technical-analysis')) {
    return 'openai/gpt-4-turbo';
  }
  if (context.type === 'empathic' && expertise.includes('empathy')) {
    return 'anthropic/claude-3-sonnet';
  }
  if (context.type === 'strategic' && expertise.includes('leadership')) {
    return 'anthropic/claude-3-opus';
  }
  if (context.type === 'analytical' && expertise.includes('logic')) {
    return 'openai/gpt-4-turbo';
  }

  // Default LLM selection
  if (expertise.includes('technical-analysis') || expertise.includes('logic')) {
    return 'openai/gpt-4-turbo';
  }
  return 'anthropic/claude-3-sonnet';
}

const optimalLLM = selectOptimalLLM(crewMember, promptContext);
const confidence = Math.random() * 0.3 + 0.7; // Simulate confidence between 0.7-1.0

return {
  ...crewTask,
  optimalLLM,
  confidence,
  reasoning: `Selected ${optimalLLM} for ${crewMember} based on ${promptContext.type} context and ${crewExpertise[crewMember].join(', ')} expertise`
};

// Process crew member response
const input = $input.first().json;
const crewTask = input.crewTask || input;
const llmResponse = input.choices?.[0]?.message?.content || 'No response generated';

return {
  crewMember: crewTask.crewMember,
  response: llmResponse,
  llmUsed: crewTask.optimalLLM,
  confidence: crewTask.confidence,
  timestamp: new Date().toISOString(),
  context: crewTask.originalPrompt,
  optimization: {
    crewMember: crewTask.crewMember,
    promptContext: JSON.stringify(crewTask.promptContext),
    personaSkills: crewTask.promptContext.domain,
    optimalLLM: crewTask.optimalLLM,
    confidence: crewTask.confidence,
    reasoning: crewTask.reasoning,
    timestamp: crewTask.timestamp
  }
};

// Collect all crew perspectives and analyze for hallucinations
const crewPerspectives = $input.all().map(item => item.json);

if (crewPerspectives.length < 2) {
  return {
    error: 'Insufficient crew responses for consensus analysis',
    perspectives: crewPerspectives
  };
}

// Calculate consensus (simplified)
const responses = crewPerspectives.map(p => p.response);
const consensusResponse = responses[Math.floor(Math.random() * responses.length)]; // Simplified consensus
const consensusConfidence = crewPerspectives.reduce((sum, p) => sum + p.confidence, 0) / crewPerspectives.length;

// Analyze for hallucinations (simplified deviation detection)
const hallucinationAnalyses = crewPerspectives.map(perspective => {
  // Simple deviation calculation based on response length and keywords
  const responseLength = perspective.response.length;
  const avgLength = responses.reduce((sum, r) => sum + r.length, 0) / responses.length;
  const lengthDeviation = Math.abs(responseLength - avgLength) / avgLength;

  // Simple keyword similarity check
  const responseWords = perspective.response.toLowerCase().split(/\s+/);
  const consensusWords = consensusResponse.toLowerCase().split(/\s+/);
  const commonWords = responseWords.filter(word => consensusWords.includes(word));
  const similarity = commonWords.length / Math.max(responseWords.length, consensusWords.length);

  const deviationScore = (lengthDeviation * 0.3) + ((1 - similarity) * 0.7);
  const isHallucination = deviationScore > 0.3; // Threshold

  return {
    crewMember: perspective.crewMember,
    isHallucination,
    deviationScore,
    consensusAlignment: 1 - deviationScore,
    correctionPrompt: isHallucination ? `Your response deviated from crew consensus. Please revise: ${consensusResponse}` : '',
    learningOpportunity: isHallucination ? `Learning opportunity for ${perspective.crewMember}: improve consensus alignment` : '',
    detectedAt: new Date().toISOString(),
    severity: deviationScore > 0.7 ? 'high' : deviationScore > 0.5 ? 'medium' : 'low'
  };
});

const hallucinationsDetected = hallucinationAnalyses.filter(a => a.isHallucination);
const overallHealth = 1 - (hallucinationsDetected.length / crewPerspectives.length);

return {
  perspectives: crewPerspectives,
  consensus: {
    consensusResponse,
    consensusConfidence,
    participantCount: crewPerspectives.length,
    agreementScore: overallHealth,
    dominantPerspective: crewPerspectives[0]?.crewMember,
    outlierCount: hallucinationsDetected.length
  },
  analyses: hallucinationAnalyses,
  overallHealth,
  hallucinationsDetected: hallucinationsDetected.length,
  processingComplete: true
};

// Process hallucination corrections
const analysisResult = $input.first().json;
const hallucinations = analysisResult.analyses.filter(a => a.isHallucination);

if (hallucinations.length === 0) {
  return {
    corrections: [],
    message: 'No corrections needed'
  };
}

// Generate corrections for hallucinating crew members
const corrections = hallucinations.map(hallucination => {
  const crewMember = hallucination.crewMember;
  const consensus = analysisResult.consensus.consensusResponse;

  // Generate corrected response (simplified)
  const correctedResponse = `After reviewing the crew consensus, I must revise my previous response. ${consensus} I acknowledge the crew's collective wisdom and incorporate these insights while maintaining my specialized perspective.`;

  return {
    crewMember,
    originalResponse: analysisResult.perspectives.find(p => p.crewMember === crewMember)?.response || '',
    correctedResponse,
    correctionReason: hallucination.learningOpportunity,
    confidence: Math.min(0.95, hallucination.consensusAlignment + 0.1),
    correctionTime: new Date().toISOString(),
    learningStored: true
  };
});

return {
  corrections,
  message: `Applied ${corrections.length} corrections`,
  processingComplete: true
};

// Store learning opportunities in RAG memory
const input = $input.first().json;
const corrections = input.corrections || [];

// Simulate RAG memory storage
const learningEntries = corrections.map(correction => ({
  id: `learning_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  crewMember: correction.crewMember,
  type: 'hallucination-correction',
  content: {
    original: correction.originalResponse,
    corrected: correction.correctedResponse,
    reason: correction.correctionReason,
    timestamp: correction.correctionTime
  },
  metadata: {
    learningType: 'hallucination-prevention',
    severity: 'medium',
    storedAt: new Date().toISOString()
  }
}));

return {
  learningEntries,
  storedCount: learningEntries.length,
  message: `Stored ${learningEntries.length} learning opportunities in RAG memory`
};

// Compile final anti-hallucination result
const analysisResult = $input.first().json;
const learningResult = $input.last().json;

return {
  success: true,
  processingId: analysisResult.perspectives[0]?.optimization?.timestamp || new Date().toISOString(),
  originalPrompt: analysisResult.perspectives[0]?.context || '',
  crewPerspectives: analysisResult.perspectives,
  hallucinationsDetected: analysisResult.analyses.filter(a => a.isHallucination),
  correctionsApplied: learningResult.corrections || [],
  consensusReached: analysisResult.overallHealth > 0.5,
  overallHealth: analysisResult.overallHealth,
  learningOpportunities: learningResult.learningEntries || [],
  systemMetrics: {
    totalPrompts: 1,
    hallucinationsDetected: analysisResult.hallucinationsDetected,
    correctionsApplied: learningResult.storedCount || 0,
    averageProcessingTime: Date.now(),
    systemHealth: analysisResult.overallHealth
  },
  timestamp: new Date().toISOString(),
  message: 'Anti-hallucination processing completed successfully'
};
