// Validate and prepare Observation Lounge session
const inputData = $input.all()[0].json;

// Validate required fields
if (!inputData.topic) {
  throw new Error('topic is required for Observation Lounge session');
}

// Extract session parameters
const topic = inputData.topic;
const context = inputData.context || {};
const crew_members = inputData.crew_members || 'all';
const discussion_type = inputData.discussion_type || 'collaborative';
const priority = inputData.priority || 'medium';

// Prepare enhanced session data
return {
  session_id: `ol_${Date.now()}`,
  topic: topic,
  context: context,
  crew_members: crew_members,
  discussion_type: discussion_type,
  priority: priority,
  timestamp: new Date().toISOString(),
  session_status: 'initialized',

  // Discussion framework
  framework: {
    objective: 'Collaborative crew decision-making',
    format: 'Structured department perspectives',
    output: 'Synthesized recommendations and action plan'
  },

  // Crew coordination parameters
  coordination: {
    max_response_time: 30000, // 30 seconds
    require_all_departments: true,
    enable_synthesis: true,
    enable_recommendations: true
  }
};

// Route to appropriate crew coordination method
const sessionData = $input.all()[0].json;
const { discussion_type, crew_members, priority } = sessionData;

// Determine routing strategy
let routing_strategy = 'full_crew';
let crew_selection = 'all';
let coordination_method = 'observation_lounge';

if (discussion_type === 'department_specific') {
  routing_strategy = 'department_focused';
  crew_selection = crew_members;
  coordination_method = 'department_meeting';
} else if (discussion_type === 'executive') {
  routing_strategy = 'command_focused';
  crew_selection = ['captain_picard', 'commander_riker', 'commander_data'];
  coordination_method = 'command_briefing';
} else if (discussion_type === 'technical') {
  routing_strategy = 'technical_focused';
  crew_selection = ['geordi_la_forge', 'commander_data', 'lieutenant_worf'];
  coordination_method = 'technical_review';
} else if (discussion_type === 'strategic') {
  routing_strategy = 'strategic_focused';
  crew_selection = ['captain_picard', 'commander_data', 'counselor_troi', 'quark'];
  coordination_method = 'strategic_planning';
}

// Priority-based adjustments
if (priority === 'high') {
  coordination_method += '_urgent';
} else if (priority === 'low') {
  coordination_method += '_standard';
}

return {
  ...sessionData,
  routing_strategy: routing_strategy,
  crew_selection: crew_selection,
  coordination_method: coordination_method,

  // Enhanced coordination parameters
  coordination_enhanced: {
    method: coordination_method,
    crew_count: Array.isArray(crew_selection) ? crew_selection.length : 'all',
    expected_duration: priority === 'high' ? 'fast' : 'standard',
    synthesis_required: true
  }
};

// Process Observation Lounge results and format for Cursor UI
const coordinatorOutput = $input.all()[0].json;
const sessionData = $input.all()[1].json;

// Check if coordination was successful
if (coordinatorOutput.error) {
  return {
    success: false,
    error: coordinatorOutput.error,
    session_id: sessionData.session_id,
    timestamp: new Date().toISOString(),
    ui_feedback: {
      message: 'Observation Lounge session failed - please try again',
      type: 'error',
      show_retry: true
    }
  };
}

// Parse the coordinator output
const result = JSON.parse(coordinatorOutput.stdout || '{}');

if (result.observation_lounge_session) {
  // Enhanced Observation Lounge response
  return {
    success: true,

    // Session information
    session: {
      id: sessionData.session_id,
      topic: sessionData.topic,
      status: result.observation_lounge_session.session_status,
      participants: result.observation_lounge_session.participants,
      total_crew: result.observation_lounge_session.total_crew,
      timestamp: result.observation_lounge_session.timestamp
    },

    // Crew insights by department
    crew_insights: result.crew_insights,

    // Synthesized discussion results
    synthesis: result.synthesis,

    // Actionable outcomes
    recommendations: result.recommendations || [],
    next_actions: result.next_actions || [],

    // UI enhancement data for Cursor
    ui_enhancements: {
      // Visual representation of crew participation
      crew_participation: {
        total_crew: result.observation_lounge_session.total_crew,
        active_participants: result.observation_lounge_session.participants,
        participation_rate: (result.observation_lounge_session.participants / result.observation_lounge_session.total_crew * 100).toFixed(1) + '%',
        status_icon: 'üöÄ'
      },

      // Department insights summary
      department_summary: Object.values(result.crew_insights || {})
        .filter(insight => insight.status === 'success')
        .map(insight => ({
          department: insight.department,
          crew_member: insight.crew_member,
          confidence: insight.confidence,
          status: 'active'
        })),

      // Discussion quality metrics
      discussion_quality: {
        synthesis_available: !!result.synthesis,
        recommendations_count: result.recommendations?.length || 0,
        actions_count: result.next_actions?.length || 0,
        quality_score: 'high'
      }
    },

    // Metadata
    metadata: {
      coordination_method: sessionData.coordination_method,
      routing_strategy: sessionData.routing_strategy,
      priority: sessionData.priority,
      discussion_type: sessionData.discussion_type
    }
  };
} else {
  return {
    success: false,
    error: 'Invalid Observation Lounge response format',
    session_id: sessionData.session_id,
    timestamp: new Date().toISOString(),
    ui_feedback: {
      message: 'Observation Lounge response format error',
      type: 'error',
      show_retry: true
    }
  };
}

// Enhanced logging and monitoring for Observation Lounge sessions
const responseData = $input.all()[0].json;
const sessionData = $input.all()[1].json;

if (responseData.success) {
  const { session, crew_insights, synthesis } = responseData;

  console.log(`üöÄ Observation Lounge Session Success:`);
  console.log(`   Session ID: ${session.id}`);
  console.log(`   Topic: ${session.topic}`);
  console.log(`   Participants: ${session.participants}/${session.total_crew}`);
  console.log(`   Status: ${session.status}`);
  console.log(`   Timestamp: ${session.timestamp}`);

  // Log crew participation by department
  const departmentStats = {};
  Object.values(crew_insights).forEach(insight => {
    if (insight.status === 'success') {
      const dept = insight.department;
      if (!departmentStats[dept]) departmentStats[dept] = 0;
      departmentStats[dept]++;
    }
  });

  console.log(`   Department Participation:`);
  Object.entries(departmentStats).forEach(([dept, count]) => {
    console.log(`     ${dept}: ${count} crew members`);
  });

  // Log synthesis availability
  if (synthesis) {
    console.log(`   Synthesis: Available`);
  } else {
    console.log(`   Synthesis: Not available`);
  }

} else {
  console.error(`‚ùå Observation Lounge Session Failed: ${responseData.error}`);
  console.error(`   Session ID: ${responseData.session_id}`);
  console.error(`   Timestamp: ${responseData.timestamp}`);
}

// Pass through the data for potential further processing
return responseData;
