// Transform crew memory payload for RAG storage
const body = $input.item.json.body;

// Handle both simple and rich crew memory formats
const isRichFormat = body.critical_decisions && body.crew_members_involved;

let ragData;

if (isRichFormat) {
  // Rich comprehensive crew memory format
  ragData = {
    session_id: body.conversation_id || `crew-memory-${Date.now()}`,
    category: 'crew_memory_comprehensive',
    title: body.title,
    executive_summary: body.executive_summary,
    session_duration: body.session_duration,
    crew_members: JSON.stringify(body.crew_members_involved || []),

    // Decisions and patterns
    critical_decisions: JSON.stringify(body.critical_decisions || []),
    bugs_fixed: JSON.stringify(body.bugs_fixed || []),
    technical_patterns: JSON.stringify(body.technical_patterns_discovered || []),

    // Knowledge
    lessons_learned: JSON.stringify(body.lessons_learned || []),
    user_insights: JSON.stringify(body.user_insights || []),
    architectural_decisions: JSON.stringify(body.architectural_decisions_for_rag || []),
    knowledge_base_entries: JSON.stringify(body.knowledge_base_entries || []),

    // Metadata
    tags: JSON.stringify([
      'crew-memory',
      'comprehensive-session',
      ...(body.key_topics || []),
      ...(body.rag_storage_metadata?.tags || [])
    ]),

    // Searchable content (full JSON for vector embedding)
    content: JSON.stringify(body),

    // Timestamps
    session_date: body.session_date || new Date().toISOString().split('T')[0],
    created_at: new Date().toISOString()
  };
} else {
  // Simple format (backward compatibility)
  ragData = {
    session_id: body.session_id || `knowledge-${Date.now()}`,
    category: body.category || 'general_knowledge',
    title: body.title || 'Untitled',
    executive_summary: body.summary || body.description || '',
    content: JSON.stringify(body),
    tags: JSON.stringify(body.tags || []),
    session_date: new Date().toISOString().split('T')[0],
    created_at: new Date().toISOString()
  };
}

console.log('ðŸ“š Ingesting knowledge:', ragData.session_id, '|', ragData.title);

return { json: ragData };
