// Parse query parameters and build search strategy
const body = $input.item.json.body;

const searchType = body.searchType || 'hybrid'; // hybrid, semantic, keyword, exact
const query = body.query || '';
const filters = body.filters || {};
const limit = body.limit || 10;

// Build search parameters
const searchParams = {
  query: query,
  search_type: searchType,

  // Filters
  category: filters.category,
  tags: filters.tags,
  crew_members: filters.crew_members,
  date_from: filters.date_from,
  date_to: filters.date_to,
  session_id: filters.session_id,

  // Pagination
  limit: limit,
  offset: filters.offset || 0,

  // Ranking
  rank_by: filters.rank_by || 'relevance' // relevance, date, votes
};

console.log('ðŸ” RAG Query:', searchType, '|', query);

return { json: searchParams };

// Format results with provenance and ranking
const items = $input.all();

const results = items.map((item, index) => {
  const data = item.json;

  // Parse JSON fields back to objects
  const parseIfString = (field) => {
    if (typeof field === 'string') {
      try { return JSON.parse(field); } catch (e) { return field; }
    }
    return field;
  };

  return {
    rank: index + 1,
    session_id: data.session_id,
    title: data.title,
    category: data.category,
    executive_summary: data.executive_summary,

    // Provenance
    crew_members: parseIfString(data.crew_members),
    session_date: data.session_date,
    created_at: data.created_at,

    // Key insights
    critical_decisions: parseIfString(data.critical_decisions),
    technical_patterns: parseIfString(data.technical_patterns),
    lessons_learned: parseIfString(data.lessons_learned),

    // Metadata
    tags: parseIfString(data.tags),

    // Relevance score (placeholder for future vector search)
    relevance_score: 1.0 - (index * 0.05)
  };
});

console.log(`âœ… Found ${results.length} results`);

return {
  json: {
    success: true,
    query: $('Parse Query').item.json.query,
    total_results: results.length,
    results: results
  }
};
