// Extract documentation generation parameters
const request = $input.first().json.body;

return {
  json: {
    doc_type: request.docType || 'getting_started',
    audience: request.audience || 'user',
    category: request.category || null,
    query: request.query || `Generate comprehensive documentation for ${request.docType} aimed at ${request.audience} audience`,
    include_diagrams: request.includeDiagrams !== false,
    include_examples: request.includeExamples !== false,
    timestamp: new Date().toISOString()
  }
};

// Combine all data sources for documentation generation
const request = $('Parse Documentation Request').item.json;
const ragKnowledge = $('Search RAG Knowledge').all();
const relationships = $('Get System Relationships').all();
const components = $('Get Active Components').all();

// Group components by layer for architecture diagrams
function groupByLayer(components) {
  const layers = {};
  components.forEach(comp => {
    const layer = comp.json.layer || 'other';
    if (!layers[layer]) {
      layers[layer] = [];
    }
    layers[layer].push(comp.json);
  });
  return layers;
}

// Generate Mermaid architecture diagram
function generateArchitectureDiagram(components, relationships) {
  const layers = groupByLayer(components);

  let diagram = 'graph TB\n';

  // Add subgraphs for each layer
  Object.entries(layers).forEach(([layerName, layerComps]) => {
    diagram += `    subgraph "${layerName.toUpperCase()}"\n`;
    layerComps.forEach(comp => {
      const compId = comp.component_name.replace(/[^a-zA-Z0-9]/g, '_');
      diagram += `        ${compId}[${comp.component_name}]\n`;
    });
    diagram += `    end\n`;
  });

  // Add relationships
  relationships.forEach(rel => {
    const sourceId = rel.json.source_component.replace(/[^a-zA-Z0-9]/g, '_');
    const targetId = rel.json.target_component.replace(/[^a-zA-Z0-9]/g, '_');
    const style = rel.json.mermaid_style === 'dashed' ? '-->' : '-->';
    const label = rel.json.protocol ? `|${rel.json.protocol}|` : '';
    diagram += `    ${sourceId} ${style}${label} ${targetId}\n`;
  });

  // Add styling
  diagram += `\n    classDef frontend fill:#3f51b5,color:#fff\n`;
  diagram += `    classDef api fill:#4caf50,color:#fff\n`;
  diagram += `    classDef integration fill:#ff9800,color:#fff\n`;
  diagram += `    classDef database fill:#9c27b0,color:#fff\n`;

  return diagram;
}

// Generate data flow sequence diagram
function generateDataFlowDiagram(relationships) {
  let diagram = 'sequenceDiagram\n';

  // Get unique participants
  const participants = new Set();
  relationships.forEach(rel => {
    participants.add(rel.json.source_component);
    participants.add(rel.json.target_component);
  });

  // Add participants
  Array.from(participants).forEach(p => {
    const pId = p.replace(/[^a-zA-Z0-9]/g, '_');
    diagram += `    participant ${pId} as ${p}\n`;
  });

  // Add interactions
  relationships.forEach(rel => {
    const sourceId = rel.json.source_component.replace(/[^a-zA-Z0-9]/g, '_');
    const targetId = rel.json.target_component.replace(/[^a-zA-Z0-9]/g, '_');
    const protocol = rel.json.protocol || 'communicates';
    diagram += `    ${sourceId}->>${targetId}: ${protocol}\n`;
  });

  return diagram;
}

// Organize knowledge by sections
function organizeKnowledgeSections(ragKnowledge) {
  const sections = {
    overview: [],
    setup: [],
    usage: [],
    examples: [],
    troubleshooting: [],
    advanced: []
  };

  ragKnowledge.forEach(item => {
    const category = item.json.category.toLowerCase();
    const content = {
      title: item.json.title,
      summary: item.json.summary,
      content: item.json.content,
      code_examples: item.json.code_examples,
      keywords: item.json.keywords
    };

    if (category.includes('overview') || category.includes('introduction')) {
      sections.overview.push(content);
    } else if (category.includes('setup') || category.includes('installation')) {
      sections.setup.push(content);
    } else if (category.includes('usage') || category.includes('guide')) {
      sections.usage.push(content);
    } else if (category.includes('example')) {
      sections.examples.push(content);
    } else if (category.includes('troubleshoot') || category.includes('issue')) {
      sections.troubleshooting.push(content);
    } else {
      sections.advanced.push(content);
    }
  });

  return sections;
}

// Generate complete documentation
const sections = organizeKnowledgeSections(ragKnowledge);
const architectureDiagram = generateArchitectureDiagram(components, relationships);
const dataFlowDiagram = generateDataFlowDiagram(relationships);

return {
  json: {
    request: request,
    knowledge_sources: ragKnowledge.length,
    relationship_count: relationships.length,
    component_count: components.length,
    sections: sections,
    diagrams: {
      architecture: architectureDiagram,
      dataFlow: dataFlowDiagram
    },
    metadata: {
      generated_at: new Date().toISOString(),
      doc_type: request.doc_type,
      audience: request.audience,
      includes_diagrams: request.include_diagrams,
      includes_examples: request.include_examples
    }
  }
};

// Generate markdown documentation from organized content
const data = $input.first().json;
const { sections, diagrams, metadata, request } = data;

// Helper function to render section
function renderSection(title, items, includeCodeExamples = true) {
  if (!items || items.length === 0) return '';

  let markdown = `\n## ${title}\n\n`;

  items.forEach(item => {
    markdown += `### ${item.title}\n\n`;
    markdown += `${item.content}\n\n`;

    if (includeCodeExamples && item.code_examples && item.code_examples.length > 0) {
      item.code_examples.forEach(example => {
        markdown += `\`\`\`${example.language || 'javascript'}\n${example.code}\n\`\`\`\n\n`;
      });
    }
  });

  return markdown;
}

// Start building markdown
let markdown = `# ${getTitleForDocType(request.doc_type, request.audience)}\n\n`;

markdown += `> **Generated:** ${new Date(metadata.generated_at).toLocaleString()}  \n`;
markdown += `> **Audience:** ${request.audience}  \n`;
markdown += `> **Knowledge Sources:** ${data.knowledge_sources} RAG entries\n\n`;

markdown += `---\n\n`;

// Add overview section
if (sections.overview.length > 0) {
  markdown += renderSection('Overview', sections.overview, false);
}

// Add system architecture diagram if applicable
if (request.include_diagrams && (request.doc_type === 'architecture' || request.doc_type === 'getting_started')) {
  markdown += `\n## System Architecture\n\n`;
  markdown += `\`\`\`mermaid\n${diagrams.architecture}\n\`\`\`\n\n`;
}

// Add setup section
if (sections.setup.length > 0) {
  markdown += renderSection('Installation & Setup', sections.setup, true);
}

// Add usage section
if (sections.usage.length > 0) {
  markdown += renderSection('Usage Guide', sections.usage, true);
}

// Add data flow diagram for integration guides
if (request.include_diagrams && request.doc_type === 'integration_guide') {
  markdown += `\n## Data Flow\n\n`;
  markdown += `\`\`\`mermaid\n${diagrams.dataFlow}\n\`\`\`\n\n`;
}

// Add examples section
if (sections.examples.length > 0 && request.include_examples) {
  markdown += renderSection('Examples', sections.examples, true);
}

// Add troubleshooting section
if (sections.troubleshooting.length > 0) {
  markdown += renderSection('Troubleshooting', sections.troubleshooting, false);
}

// Add advanced section
if (sections.advanced.length > 0) {
  markdown += renderSection('Advanced Topics', sections.advanced, true);
}

// Add footer
markdown += `\n---\n\n`;
markdown += `*This documentation was automatically generated from ${data.knowledge_sources} knowledge sources in the RAG memory system.*\n\n`;
markdown += `*Last updated: ${new Date(metadata.generated_at).toLocaleString()}*\n`;

// Helper function for titles
function getTitleForDocType(docType, audience) {
  const titles = {
    'getting_started': `Getting Started Guide`,
    'installation': `Installation Guide`,
    'configuration': `Configuration Guide`,
    'architecture': `System Architecture Documentation`,
    'api_reference': `API Reference`,
    'integration_guide': `Integration Guide`,
    'user_guide': `User Guide`,
    'developer_guide': `Developer Guide`,
    'contributor_guide': `Contributor Guide`,
    'troubleshooting': `Troubleshooting Guide`,
    'deployment': `Deployment Guide`
  };

  let title = titles[docType] || 'Documentation';
  if (audience && audience !== 'all') {
    title += ` (${audience.charAt(0).toUpperCase() + audience.slice(1)})`;
  }

  return title;
}

return {
  json: {
    markdown: markdown,
    metadata: metadata,
    file_name: `${request.doc_type}-${request.audience}.md`,
    file_path: `docs/${getPathForDocType(request.doc_type)}/${request.doc_type}-${request.audience}.md`,
    source_count: data.knowledge_sources
  }
};

// Helper function for file paths
function getPathForDocType(docType) {
  const paths = {
    'getting_started': 'getting-started',
    'installation': 'getting-started',
    'configuration': 'getting-started',
    'architecture': 'architecture',
    'api_reference': 'api-reference',
    'integration_guide': 'integrations',
    'user_guide': 'user-guide',
    'developer_guide': 'developer-guide',
    'contributor_guide': 'contributor-guide',
    'troubleshooting': 'troubleshooting',
    'deployment': 'deployment'
  };

  return paths[docType] || 'general';
}

// Prepare success response
const data = $input.first().json;

return {
  json: {
    success: true,
    message: 'Documentation generated successfully',
    file_path: data.file_path,
    file_name: data.file_name,
    source_count: data.source_count,
    generated_at: data.metadata.generated_at,
    markdown: data.markdown,
    metadata: data.metadata
  }
};
