// Democratic LLM Selection Algorithm
const task = $json.task;
const availableModels = $json.collaboration_context.available_models;
const budgetConstraints = $json.collaboration_context.budget_constraints;

// Model configurations with OpenRouter IDs
const models = {
  'claude-sonnet': {
    platform: 'anthropic',
    openrouter_id: 'anthropic/claude-3.5-sonnet',
    cost_per_token: 0.000003,
    specialization: 'strategic_analysis',
    strengths: ['reasoning', 'analysis', 'coding', 'writing']
  },
  'gpt-4o': {
    platform: 'openai', 
    openrouter_id: 'openai/gpt-4o',
    cost_per_token: 0.000005,
    specialization: 'research',
    strengths: ['multimodal', 'creativity', 'general_purpose']
  },
  'gemini-pro': {
    platform: 'google',
    openrouter_id: 'google/gemini-pro-1.5',
    cost_per_token: 0.000002,
    specialization: 'optimization', 
    strengths: ['code_analysis', 'performance', 'efficiency']
  },
  'llama-3': {
    platform: 'meta',
    openrouter_id: 'meta-llama/llama-3-70b-instruct',
    cost_per_token: 0.000001,
    specialization: 'code_implementation',
    strengths: ['open_source', 'cost_effective', 'coding']
  }
};

// Task-model affinity scoring
const taskAffinities = {
  'code_implementation': {
    'llama-3': 0.95,
    'claude-sonnet': 0.85,
    'gemini-pro': 0.80,
    'gpt-4o': 0.70
  },
  'strategic_analysis': {
    'claude-sonnet': 0.98,
    'gpt-4o': 0.90,
    'gemini-pro': 0.75,
    'llama-3': 0.65
  },
  'research': {
    'gpt-4o': 0.95,
    'claude-sonnet': 0.85,
    'gemini-pro': 0.75,
    'llama-3': 0.60
  },
  'optimization': {
    'gemini-pro': 0.95,
    'llama-3': 0.85,
    'claude-sonnet': 0.80,
    'gpt-4o': 0.75
  }
};

// Democratic selection algorithm
function selectBestModel(task, availableModels) {
  const taskType = task.type;
  const complexity = task.complexity;

  let scores = {};

  // Calculate base scores from task affinity
  if (taskAffinities[taskType]) {
    scores = { ...taskAffinities[taskType] };
  } else {
    // Default scoring for unknown task types
    availableModels.forEach(model => {
      scores[model] = 0.7; // neutral score
    });
  }

  // Adjust for complexity
  const complexityMultiplier = {
    'low': 0.9,
    'medium': 1.0,
    'high': 1.1
  }[complexity] || 1.0;

  // Apply complexity adjustment and cost considerations
  Object.keys(scores).forEach(modelName => {
    if (models[modelName]) {
      scores[modelName] *= complexityMultiplier;

      // Cost efficiency bonus for budget-conscious selections
      const costEfficiency = 1 / (models[modelName].cost_per_token * 1000000); // normalize
      scores[modelName] += costEfficiency * 0.1; // small cost bonus
    }
  });

  // Find the best model
  const bestModel = Object.entries(scores)
    .filter(([model]) => availableModels.includes(model))
    .sort(([,a], [,b]) => b - a)[0];

  return {
    selected_model: bestModel[0],
    confidence: bestModel[1],
    all_scores: scores,
    model_config: models[bestModel[0]]
  };
}

// Perform selection
const selection = selectBestModel(task, availableModels);

// Estimate cost
const estimatedTokens = {
  'low': 500,
  'medium': 1500,
  'high': 3000
}[task.complexity] || 1500;

const estimatedCost = estimatedTokens * selection.model_config.cost_per_token;

return [{
  collaboration_id: $json.collaboration_id,
  session_id: $json.session_id,
  selected_model: selection.selected_model,
  confidence_score: selection.confidence,
  model_config: selection.model_config,
  estimated_cost: estimatedCost,
  estimated_tokens: estimatedTokens,
  routing_decision: {
    primary_model: selection.selected_model,
    fallback_models: Object.entries(selection.all_scores)
      .filter(([model]) => model !== selection.selected_model && availableModels.includes(model))
      .sort(([,a], [,b]) => b - a)
      .slice(0, 2)
      .map(([model]) => model),
    democratic_scores: selection.all_scores
  },
  task: task
}];

// Response Aggregation and Quality Assessment
const input = $input.all();
const originalData = input[0]; // Contains our routing decision
const llmResponse = input[0]; // Contains the LLM response

// Parse the LLM response based on the platform
let parsedResponse;
let usage = {};

if (originalData.selected_model === 'claude-sonnet') {
  // Claude API response format
  parsedResponse = {
    content: llmResponse.content?.[0]?.text || llmResponse.text || 'No response',
    model: llmResponse.model || 'claude-3-5-sonnet-20241022',
    usage: llmResponse.usage || {}
  };
} else {
  // OpenRouter API response format (OpenAI compatible)
  parsedResponse = {
    content: llmResponse.choices?.[0]?.message?.content || 'No response',
    model: llmResponse.model || originalData.model_config.openrouter_id,
    usage: llmResponse.usage || {}
  };
}

// Calculate actual cost based on usage
const actualTokens = parsedResponse.usage.total_tokens || originalData.estimated_tokens;
const actualCost = actualTokens * originalData.model_config.cost_per_token;

// Quality assessment
const qualityMetrics = {
  response_length: parsedResponse.content.length,
  estimated_vs_actual_tokens: {
    estimated: originalData.estimated_tokens,
    actual: actualTokens,
    accuracy: Math.abs(1 - (actualTokens / originalData.estimated_tokens))
  },
  cost_efficiency: {
    estimated: originalData.estimated_cost,
    actual: actualCost,
    savings: originalData.estimated_cost - actualCost
  },
  model_confidence: originalData.confidence_score
};

// Final collaboration result
const collaborationResult = {
  collaboration_id: originalData.collaboration_id,
  session_id: originalData.session_id,
  timestamp: new Date().toISOString(),

  // Democratic selection results
  democratic_selection: {
    selected_model: originalData.selected_model,
    confidence_score: originalData.confidence_score,
    routing_decision: originalData.routing_decision,
    selection_rationale: `Selected ${originalData.selected_model} with ${(originalData.confidence_score * 100).toFixed(1)}% confidence for ${originalData.task.type} task`
  },

  // LLM Response
  llm_response: {
    content: parsedResponse.content,
    model: parsedResponse.model,
    platform: originalData.model_config.platform
  },

  // Cost and usage analytics
  analytics: {
    usage: parsedResponse.usage,
    cost_analysis: qualityMetrics.cost_efficiency,
    token_prediction_accuracy: qualityMetrics.estimated_vs_actual_tokens.accuracy,
    quality_score: Math.min(1.0, originalData.confidence_score * (1 - qualityMetrics.estimated_vs_actual_tokens.accuracy * 0.1))
  },

  // Task metadata
  task_metadata: {
    task_id: originalData.task.task_id,
    task_type: originalData.task.type,
    complexity: originalData.task.complexity,
    completion_time: new Date().toISOString()
  },

  // System metadata for learning
  system_metadata: {
    n8n_workflow: 'LLM_Democratic_Collaboration',
    integration_version: '1.0.0',
    collaboration_mode: 'democratic_selection',
    success: true
  }
};

return [collaborationResult];
